import React from 'react'

export const Transaction = (props) => {
    const sign = props.transaction.amount < 0 ? '-' : '+';

    return (
        <li className="minus">
            {props.transaction.text} <span> {sign}Rp{props.transaction.amount} </span> <button className="delete-btn">x</button>
        </li>
    )
}
