import React from 'react'

export const Transaction = (props) => {
    const sign = props.transaction.amount < 0 ? '-' : '+';

    return (
        <li className={sign == '-' ? 'minus' : 'plus'}>
            {props.transaction.text} <span> {sign}Rp{Math.abs(props.transaction.amount)} </span> <button className="delete-btn">x</button>
        </li>
    )
}
